import React, {useState} from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import UwcNavbar from "../uwc-navbar/uwc-navbar"
import "./vehicle-assign.css";

const VehicleAssign = () => {
    const params = useParams();
    const navigate = useNavigate();
    var state_clicked = 'clicked', state_unclick = 'unclick';
    const truckList = [
        {id: 0, state: state_unclick}, 
        {id: 1, state: state_unclick},
        {id: 2, state: state_unclick},
        {id: 3, state: state_unclick}
    ];
    const trollerList = [
        {id: 0, state: state_unclick}, 
        {id: 1, state: state_unclick},
        {id: 2, state: state_unclick},
        {id: 3, state: state_unclick}
    ];

    var [rows, setRows] = useState([]);

    if (params.staffType === "janitor") { //janitor --> troller
        // rows = trollerList.reduce(function (rows, key, index) { 
        //     return (index % 2 === 0 ? rows.push([key]) 
        //     : rows[rows.length-1].push(key)) && rows;
        // }, []);
        setRows(trollerList);
    }
    else if (params.staffType === "collector") { //collector --> truck
        // rows = truckList.reduce(function (rows, key, index) { 
        //     return (index % 2 === 0 ? rows.push([key]) 
        //     : rows[rows.length-1].push(key)) && rows;
        // }, []);
        setRows(truckList);
    }

    const HandleOnClick = (id) => {
        var newRows = [];
        for (var i=0; i < rows.length; i++) {
            var row = rows[i];
            if (row.id === id) row.state = state_clicked;
            else row.state = state_unclick;
            newRows.concat(row);
        }
        setRows(newRows);       
        console.log(newRows);

        navigate('/vehicle-assign/'+params.staffType, {replace: true});
    }

    // const HandleOnClick = (id) => {
    //     setRows((row) => 
    //     row.id === id ? row.state = state_clicked : row.state = state_unclick
    //     );
    //     // console.log(rows);
    // };

    const DisplayList = () => {
        let content;
        console.log(rows);
        // for (let index=0; index<rows.length; index+2) {
        //     content.push(<tr key={''+rows[index].id+rows[index+1].id} className="vehicle-table-row">
        //                     <td key={rows[index].id} className={`vehicle-table-data ${rows[index].state !== state_unclick ? 'table-data-clicked' : ''}`}
        //                         onClick={() => HandleOnClick(rows[index].id)}>
        //                         Vehicle ID #{rows[index].id}
        //                     </td>
        //                     <td key={rows[index+1].id} className={`vehicle-table-data ${rows[index+1].state !== state_unclick ? 'table-data-clicked' : ''}`}
        //                         onClick={() => HandleOnClick(rows[index+1].id)}>
        //                         Vehicle ID #{rows[index+1].id}
        //                     </td>
        //                 </tr>)
        // }
        console.log(content);
        return (<h1>Hello</h1>);
    };

    return (
      <div className="route-management">
        <UwcNavbar/>
        <span className="vehicle-assign-title">VEHICLE ASSIGNMENT</span>
        <div className="vehicle-assign-body">
            <div className="vehicle-group">
                <span className="vehicle-group-title">
                    {params.staffType === "janitor" ? "Troller" : "Truck"}
                </span>
                <table className="vehicle-table">
                    <colgroup>
                      <col span="1" style={{width: 40 + '%'}} />
                      <col span="1" style={{width: 40 + '%'}} />
                    </colgroup>
                    <tbody>
                        {/* {rows.map((row, index) => (
                            <tr key={''+rows[index].id+rows[index+1].id} className="vehicle-table-row">
                                <td key={rows[index].id} className={`vehicle-table-data ${rows[index].state !== state_unclick ? 'table-data-clicked' : ''}`}
                                    onClick={() => HandleOnClick(rows[index].id)}>
                                    Vehicle ID #{rows[index].id}
                                </td>
                                <td key={rows[index+1].id} className={`vehicle-table-data ${rows[index+1].state !== state_unclick ? 'table-data-clicked' : ''}`}
                                    onClick={() => HandleOnClick(rows[index+1].id)}>
                                    Vehicle ID #{rows[index+1].id}
                                </td>
                            </tr>
                        ))} */}
                        <DisplayList />
                    </tbody>
                </table>
            </div>
              
            <div className="btn-submit">
                <div className="btn-body">
                    <span className="btn-cancel">Cancel</span>
                </div>
                <div className="btn-body">
                    <span className="btn-save">Save</span>
                </div>
            </div>
        </div>
      </div>
    );
};
export default VehicleAssign;
